import { h, render } from 'preact-cycle';

import createChannelHandler from './createChannelHandler';

const CHAT_CHANNEL_NAME = 'chat';

// https://stackoverflow.com/questions/879152/how-do-i-make-javascript-beep
const notificationSound =  new Audio("data:audio/wav;base64,");

// jshint ignore:start
const {
  ADD_CHAT_MESSAGE,
  SEND_CHAT_MESSAGE,
  CHAT_MESSAGE_INPUT,
  ITEM_DROPPED,
  ADD_FILE
} = {
  ADD_CHAT_MESSAGE: (_, chat, type, {data}) => {
    console.log('_', _);
    chat.messages.unshift({type, data, time: new Date().getTime()});
    notificationSound.play();
  },

  SEND_CHAT_MESSAGE: (_, chat) => {
    console.log('send');
    const {message} = chat.input;

    ADD_CHAT_MESSAGE(_, chat, 'self', {data: message});

    chat.channel.send(message);
    chat.input.message = '';
  },

  CHAT_MESSAGE_INPUT: (_, chat, {target:{value}}) => {
    console.log('input', _);
    chat.input.message = value;
  },

  ITEM_DROPPED: (_, chat, event) => {
    console.log('dropped', event);
    event.preventDefault();
  },

  ADD_FILE: (_, chat) => {
    const fileInput = document.createElement('input');
    fileInput.type = 'file';
    fileInput.addEventListener('change', ({target:{files}}) => {
      console.log('files', files);

      Array.prototype.forEach.call(files, file => {
        const reader = new FileReader();
        reader.addEventListener('load', ({target:{result}}) => {
          ADD_CHAT_MESSAGE(_, chat, 'self', {data: result});

          chat.channel.send(result);
        });
        reader.readAsDataURL(file);
      });
    });
    fileInput.click();
  }
};
// jshint ignore: end

const CHAT_CHANNEL =
  createChannelHandler(
    CHAT_CHANNEL_NAME,
    ADD_CHAT_MESSAGE,
    (partner, channel) =>
      ({
        partner,
        channel,
        start: new Date().getTime(),
        messages: [],
        input: {
          message: undefined
        }
      }));

// jshint ignore:start
const Chat = ({chat}, {mutation}) => (
  <chat onDrop={mutation(ITEM_DROPPED, chat)} onDragOver={event => event.preventDefault()}>
    <inputs>
      <form onSubmit={mutation(SEND_CHAT_MESSAGE, chat)} action="javascript:" autoFocus>
        <input type="text" value={chat.input.message} onInput={mutation(CHAT_MESSAGE_INPUT, chat)} placeholder="Type your chat message here..." />
      </form>
      <button onClick={mutation(ADD_FILE, chat)}>+</button>
    </inputs>
    <Messages messages={chat.messages} start={chat.start} />
  </chat>
);
// jshint ignore: end

// jshint ignore:start
const Messages = ({messages, start}) => (
  <messages>
    {messages.map(({type, data, time}) => (
      <message className={type}>
        <container className={`message-time-${5 * Math.round(100 * (time - start) / (new Date().getTime() - start) / 5)}`}>
          {/^data:image\/png;base64,.*$/.test(data) ? <img src={data} /> :<data>{data}</data>}
          <time>{new Date(time).toISOString()}</time>
        </container>
      </message>
    ))}
  </messages>
);
// jshint ignore:end


export {Chat, CHAT_CHANNEL, CHAT_CHANNEL_NAME};